from bs4 import BeautifulSoup
import pandas as pd

def scrap(file, path):
	notScrapable = {}
	with open(f"{path}" + f"/{file}", 'r', encoding="utf-8") as htmlFile:	
		content = htmlFile.read()	
		content.encode("utf-8")
		soup = BeautifulSoup(content, "lxml")

		"psudoScrap"
		mydivs = soup.find_all("div", {"class":"content"})
		if mydivs == [] or mydivs is None:
			notScrapable[f"{path}"] = f"{file}"
		
		# QuestionText
		imgQuestionArr = []
		question_text = []
		qtexts = soup.find_all("div", {"class" : "qtext"})
		for qTag in qtexts:
			imgQPaths = []
			
			"Look for the images in the questions"
			for img in qTag.find_all("img"):
				imgQPaths.append(img['src'])
				print(img['src'])
			imgQuestionArr.append(imgQPaths)

			"Look for all qText"
			" * Nota: Necesitamos identificar un <img> tag para saber el tipo de qText"
			qTag = qTag.text
			question_text.append(qTag)

		# Anwers
		imgAnswersArr = []
		anwers_notCorrect = []
		imagesInAnwers = soup.find_all(["div"], {"class": "answer"})
		for answerTag in imagesInAnwers:
			img_path_arr = []
			answers_arr = []

			"Look for the images in the answers"
			imgInAnswerTag = answerTag.find_all("img")
			if imgInAnswerTag == [] or imgInAnswerTag is None:
				img_path_arr.append(None)
			else:
				for img in imgInAnswerTag:
					#print(img['src'])
					img_path_arr.append(img['src'])

			"Look for all answers"
			" * Nota: necesitamos agrupar todas las respuestas en un [] contenidas dentro del div asnwer, teniendo así cada texto con todas las posibilidades"
			" * Nota: Necesitamos identificar un <img> tag para saber el tipo de answer"
			for answer in answerTag.find_all("div", {"class": ["r0", "r1"]}):  # without "r1 correct", "r0 correct"
				answer = answer.text[3:]
				answers_arr.append(answer)

			imgAnswersArr.append(img_path_arr)
			anwers_notCorrect.append(answers_arr)
		# print(imgAnswersArr)
		# print(anwers_notCorrect)

		"Look for all Correct anwers"
		correctAnwersArr = []
		correctAnwers = soup.find_all("div", {"class":  "rightanswer"})
		for correct in correctAnwers:
			correct = correct.text
			print(correct)
			correctAnwersArr.append(correct)
		
		"Look for all feedbacks"
		feedbacksArr = []
		feedbacks = soup.find_all("div", {"class": "specificfeedback"})
		for feedback in feedbacks:
			feedback = feedback.text
			print(feedback)
			feedbacksArr.append(feedback)

		print(list(map(len, (imgQuestionArr , question_text, imgAnswersArr, anwers_notCorrect, correctAnwersArr, feedbacksArr))))
		collection = createDataFrame(QIpaths=imgQuestionArr , Question=question_text, AIpaths=imgAnswersArr, Answers=anwers_notCorrect, Correct=correctAnwersArr, Feedback=feedbacksArr)
		return collection

	print("No se han podido procesar",len(notScrapable), "html's")
	print(notScrapable)

def createDataFrame(QIpaths, Question,  AIpaths, Answers, Correct, Feedback): #, QuestionImg=[], AnwerImg=[] booleans for existence of img's
	"DataFrame"
	dataframe = pd.DataFrame(data={
			#"QuestionImg": QuestionImg, # bool
			"QIpaths": QIpaths,			# text => img
			"Question": Question,		# text
			#"AnwerImg": AnwerImg,		# bool
			"AIpaths": AIpaths,			# text => img
			"Answers": Answers,			# text
			"Correct": Correct,			# text
			"Feedback": Feedback,		# text
		})
	return dataframe
	

